<script>
    function ReadyReceivedForReword(){
        $.ajaxSetup({
            headers: { 'X-CSRF-Token' : $('meta[name=csrf-token]').attr('content') }
        });
        $.ajax({
            url:'{{route('received.tareget.member.registered')}}',
